function setRodoCookie() {
    var consentString;
    consentString = readCookie('eupubconsent');
    if (consentString != null) {
        var consentObject = decodeConsentString(consentString);
        var allowedPurposeIds = consentObject.allowedPurposeIds;
        console.log(allowedPurposeIds);
        var rodoCookiePrev = readCookie('rodo2019');
        var rodoCookieNew;
        var rodoCookieDays;
        if (allowedPurposeIds === undefined || allowedPurposeIds.length <= 2) {
            rodoCookieNew = 0;
            rodoCookieDays = 14;
        } else if (allowedPurposeIds.length == 5) {
            rodoCookieNew = 1;
            rodoCookieDays = 30;
        } else if ((allowedPurposeIds.indexOf(14) != -1) && (allowedPurposeIds.indexOf(16) != -1)) {
            rodoCookieNew = 210;
            rodoCookieDays = 30;
        } else if (allowedPurposeIds.indexOf(15) != -1) {
            rodoCookieNew = 201;
            rodoCookieDays = 30;
        }
        createCookie('rodo2019', rodoCookieNew, rodoCookieDays);
        if (rodoCookiePrev != rodoCookieNew) {
            window.location.reload(true);
        }
    }
}

